import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { MusicServiceService } from '../service/music-service.service';

import { Artist } from '../models/Artist';

@Component({
	selector: 'app-list-artists',
	templateUrl: './list-artists.component.html',
	styleUrls: ['./list-artists.component.css']
})
export class ListArtistsComponent implements OnInit {
	artists: Artist[] = [];
  	selectedArtist: Artist | null = null;

	constructor(private route: ActivatedRoute, private service: MusicServiceService,private router: Router) { }

	ngOnInit() {
    console.log("Getting data....");
		this.service.getArtist((artist: Artist[]) => {
      this.artists = artist;
      console.log(artist);
    });
	}

  onSelectFruit(selectedArtist: artist){
    this.selectedArtist = selectedArtist;
    console.log("User selected" + selectedArtist);

  }
}
