import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent
{
  title = 'My Music Collection';
  version = "1.0";

  constructor(private router: Router)
  {

  }

  public displayVersion()
  {
    alert(this.title + " Version: " + this.version);
  }

  public displayArtistList()
  {
    this.router.navigate(['list-artists'], { queryParams: { data: new Date()} });
  }
}
<div class="container">

  <body>

    <nav class="navbar navbar-expand-xxl navbar-dark dark-bg">
      <a class="navbar-brand brand-name" href="#">Music App</a>


      <div class="collapse navbar-collapse">
        <ul class="navbar-nav navbar-nav-scroll">
          <li class="navbar-links">
            <a class="nav-link" href="https://www.gcu.edu/">GCU Home Page</a>
          </li>
          <li class="navbar-links">
            <a class="nav-link" routerLink="/create">Create</a>
          </li>
          <li class="navbar-links">
            <a class="nav-link" routerLink="/update">Update</a>
          </li>
          <li class="navbar-links">
            <a class="nav-link" routerLink="/delete">Delete</a>
          </li>
          <li class="navbar-links">
            <button class="nav-link" (click)="displayArtistList()">Search</button>
          </li>
          <li class="navbar-links">
            <button class="nav-link" (click)="displayVersion()">About</button>
          </li>
        </ul>
      </div>
    </nav>
  </body>

</div>
