import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent
{
  title = 'My Music Collection';
  version = "1.0";

  constructor(private router: Router)
  {

  }

  public displayVersion()
  {
    // Display Version in a JavaScript Alertbox
    alert(this.title + " Version: " + this.version);
  }

  public displayArtistList()
  {
    // Navigate to the List Artist Component and tack on a timestamp so the component gets reinitilized
    this.router.navigate(['list-artists'], { queryParams: { data: new Date()} });
  }
}
<div class="container">
  <div class="header">
    <h1>Welcome to {{ title }}</h1>
  </div>
  <nav class="navbar navbar-expand-lg navbar-dark bg-dark">

    <div class="d-flex justify-content-center" id="navbarNav">
      <div style="margin: 0 auto;">
      <div class="navbar-nav">
        <a class="submit-button text-center" href="#">My Music</a>
        <a class="submit-button text-center" href="https://www.gcu.edu" target="_blank">GCU Home Page</a>
        <a class="submit-button text-center" routerLink="/create">Create Album</a>
        <a class="submit-button text-center" (click)="displayArtistList()">Artist List</a>
        <button class="submit-button text-center" (click)="displayVersion()">About</button>
      </div>
    </div>
    </div>
  </nav>
  <div class="content">
    <router-outlet></router-outlet>
  </div>
</div>
